                  <td>
                    {/* {item.products.map((a) => {
                            return (
                              <>
                                <span>{a.productName}</span>
                                <br />
                              </>
                            );
                          })} */}
                    lipstick
                  </td>
                  <td>
                    coimbatore,India
                    {/* {item.address.city},{item.address.country} */}
                  </td>
                  <td>
                    today
                    {/* {new Date(item.createdAt).toDateString()} */}
                  </td>
                </tr>
                {/* ); */}
                {/* })} */}
              </tbody>
            </table>
            {/* ) : (
              <>
                <h2 style={{ textAlign: "center", width: "100%" }}>
                  Your have not yet ordered any items
                </h2>
              </>
            )} */}
          </Container>
        )}
      </MainContainer>
      <Newsletter />
      <Footer />
    </>
  );
};

export default Order;
